mapper.select:
SELECT poc.chks_id AS chksId, oo.olet_name_l1 AS outlet, pbd.bday_date AS date, chks_check_prefix_num AS checkNo, pct.ctbl_table AS tableNo, pct.ctbl_table_ext as tableExt, poc.chks_item_total AS checkItemTotal, poc.chks_check_total AS checkTotal, pop.cpay_name_l1 AS paymentType, pop.cpay_pay_total AS amount, pop.cpay_pay_tips AS tips, poc.chks_pre_disc + poc.chks_post_disc + poc.chks_mid_disc AS checkDiscountTotal FROM pos_check_items pci LEFT JOIN pos_checks poc ON poc.chks_id = pci.citm_chks_id LEFT JOIN pos_check_payments pop ON poc.chks_id = pop.cpay_chks_id LEFT JOIN pos_business_days pbd ON poc.chks_bday_id = pbd.bday_id LEFT JOIN pos_check_tables pct ON poc.chks_id = pct.ctbl_chks_id LEFT JOIN hero_outlet.out_outlets oo ON poc.chks_olet_id = oo.olet_id LEFT JOIN pos_check_discounts pcd ON pcd.cdis_citm_id = pci.citm_id LEFT JOIN pos_check_discounts_items pcdi ON pcd.cdis_id = pcdi.cdit_cdis_id WHERE ( SELECT sum( pci1.citm_pre_disc + pci1.citm_post_disc + pci1.citm_mid_disc ) FROM pos_check_items pci1 WHERE pcd.cdis_citm_id = pci1.citm_id ) != ( SELECT sum( pcdi1.cdit_round_total ) FROM pos_check_discounts_items pcdi1 WHERE pcd.cdis_id = pcdi1.cdit_cdis_id )
and poc.chks_close_time <= #{checkEndTime} and poc.chks_close_time >=#{checkBeginTime}
and poc.chks_open_time <= #{checkEndTime} and poc.chks_open_time >=#{checkBeginTime}
and pbd.bday_date <= #{businessDayEndDate} and pbd.bday_date >=#{businessDayBeginDate}
group by pop.cpay_id
