mapper.select:
select

from pos_checks where 1=1

	and chks_check_prefix_num = #{checkNum}


	and chks_olet_id =#{outletId}


	and chks_bday_id =#{sDayId}



		and chks_open_time <= #{dendTime} and chks_open_time >=#{dbeginTime}




		and chks_close_time <= #{dendTime} and chks_close_time >=#{dbeginTime}

SELECT poc.chks_id as chksId, oo.olet_name_l1 as outlet , pbd.bday_date as date, chks_check_prefix_num as checkNo, pct.ctbl_table as tableNo, pct.ctbl_table_ext as tableExt, poc.chks_item_total as checkItemTotal, poc.chks_check_total as checkTotal, pop.cpay_name_l1 as paymentType, pop.cpay_pay_total as amount, pop.cpay_pay_tips as tips, poc.chks_pre_disc+poc.chks_post_disc+poc.chks_mid_disc as checkDiscountTotal FROM pos_checks poc LEFT JOIN pos_check_payments pop ON poc.chks_id = pop.cpay_chks_id LEFT JOIN pos_business_days pbd ON poc.chks_bday_id = pbd.bday_id LEFT JOIN pos_check_tables pct ON poc.chks_id = pct.ctbl_chks_id LEFT JOIN hero_outlet.out_outlets oo on poc.chks_olet_id = oo.olet_id WHERE poc.chks_check_total != ( SELECT sum( pop1.cpay_pay_total ) FROM pos_check_payments pop1 WHERE poc.chks_id = pop1.cpay_chks_id )
and chks_close_time <= #{checkEndTime} and chks_close_time >=#{checkBeginTime}
and chks_open_time <= #{checkEndTime} and chks_open_time >=#{checkBeginTime}
and pbd.bday_date <= #{businessDayEndDate} and pbd.bday_date >=#{businessDayBeginDate}

SELECT poc.chks_id as chksId, oo.olet_name_l1 as outlet , pbd.bday_date as date, chks_check_prefix_num as checkNo, pct.ctbl_table as tableNo, pct.ctbl_table_ext as tableExt, poc.chks_item_total as checkItemTotal, poc.chks_check_total as checkTotal, pop.cpay_name_l1 as paymentType, pop.cpay_pay_total as amount, pop.cpay_pay_tips as tips, poc.chks_pre_disc+poc.chks_post_disc+poc.chks_mid_disc as checkDiscountTotal FROM pos_checks poc LEFT JOIN pos_check_payments pop ON poc.chks_id = pop.cpay_chks_id LEFT JOIN pos_business_days pbd ON poc.chks_bday_id = pbd.bday_id LEFT JOIN pos_check_tables pct ON poc.chks_id = pct.ctbl_chks_id LEFT JOIN hero_outlet.out_outlets oo on poc.chks_olet_id = oo.olet_id LEFT JOIN pos_check_items pci ON pci.citm_chks_id = poc.chks_id WHERE poc.chks_item_total != (select sum(pci1.citm_round_total) from pos_check_items pci1 where poc.chks_id = pci1.citm_chks_id)
and chks_close_time <= #{checkEndTime} and chks_close_time >=#{checkBeginTime}
and chks_open_time <= #{checkEndTime} and chks_open_time >=#{checkBeginTime}
and pbd.bday_date <= #{businessDayEndDate} and pbd.bday_date >=#{businessDayBeginDate} group by pop.cpay_id

SELECT poc.chks_id as chksId, oo.olet_name_l1 as outlet , pbd.bday_date as date, chks_check_prefix_num as checkNo, pct.ctbl_table as tableNo, pct.ctbl_table_ext as tableExt, poc.chks_item_total as checkItemTotal, poc.chks_check_total as checkTotal, pop.cpay_name_l1 as paymentType, pop.cpay_pay_total as amount, pop.cpay_pay_tips as tips, poc.chks_pre_disc+poc.chks_post_disc+poc.chks_mid_disc as checkDiscountTotal FROM pos_checks poc LEFT JOIN pos_check_payments pop ON poc.chks_id = pop.cpay_chks_id LEFT JOIN pos_business_days pbd ON poc.chks_bday_id = pbd.bday_id LEFT JOIN pos_check_tables pct ON poc.chks_id = pct.ctbl_chks_id LEFT JOIN hero_outlet.out_outlets oo on poc.chks_olet_id = oo.olet_id LEFT JOIN pos_check_discounts pcd ON pcd.cdis_chks_id = poc.chks_id WHERE poc.chks_pre_disc+poc.chks_post_disc+poc.chks_mid_disc != (select sum(pcd1.cdis_round_total) from pos_check_discounts pcd1 where poc.chks_id = pcd1.cdis_chks_id)
and chks_close_time <= #{checkEndTime} and chks_close_time >=#{checkBeginTime}
and chks_open_time <= #{checkEndTime} and chks_open_time >=#{checkBeginTime}
and pbd.bday_date <= #{businessDayEndDate} and pbd.bday_date >=#{businessDayBeginDate} group by pop.cpay_id
